<?phpfunction storeAddress(){		if(!$_GET['email']){ return "No email address provided"; } 	if(!preg_match("/^[_a-z0-9-]+(\.[_a-z0-9-]+)*@[a-z0-9-]+(\.[a-z0-9-]+)*$/i", $_GET['email'])) {		return "Email address is invalid"; 	}	require_once('MCAPI.class.php');		$api = new MCAPI('5282a4e7fb776fd97f067ab7e994ce9a-us3');			$list_id = "75677bb3b7";	if($api->listSubscribe($list_id, $_GET['email'], '') === true) {		return 'Thank you for signing up for SoHacks';	}else{			return 'Error: ' . $api->errorMessage;	}	}// If being called via ajax, autorun the functionif($_GET['ajax']){ echo storeAddress(); }?>